import React, { useEffect, useState } from 'react'

const ExUseEffect = () => {
    const[count, setCount] = useState(0);
    useEffect(()=>{
        // setCount(1000);
        setTimeout(()=>{ //to change the value of count after 4 seconds we use setTimeOut
            setCount(1000);
        },4000)
    })
  return (
    <div>ExUseEffect
        <h1>{count}</h1>
    </div>
  )
}

export default ExUseEffect
