import React, { useEffect, useState } from 'react'

const UseEffectEx = () => {
  const [count, setCount] = useState(0);
  useEffect(() => {
    // setCount(1000);
    setTimeout(() => { //to change the value of count after 4 seconds we use setTimeOut
      setCount(1000);
    }, 9000)
  })
  return (
    <div>UseEffectEx
      <h1>{count}</h1>
    </div>
  )
}

export default UseEffectEx
